package variablestudy;

public class NumberNonDecimal {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//number+Non decimal
		//byte--> 1byte--> -128 to 127 
		
		byte empID;// 1.variable declaration
		
		empID=50;//2.variable initialization 
		
		System.out.println("My Emp ID is "+empID);//3. usage
		
		System.out.println("-------");
		
		// int-->4byte-->-2,147,483,648 to 2,147,483, 647
		
		int run;// 1.variable declaration
		
		run=123456789;//2.variable initialization
		
		System.out.println("Runs are "+run);//3.variable usage
	}

}
